    render(){
        const post = this.state.post ? (
            <div className="post">
                <h4 className="center">{this.state.post.title}</h4>
                <h6>{this.state.post.body}</h6>
            </div>
        ): (
            <div className="container">no post</div>
        )
        return(
            <div className="container">
                <h4>{post}</h4>
            </div>
        )
    }
}

export default Post
